// Full updated insurance_bot.jsx with all fixes

import React, { useState, useEffect, useRef } from 'react';
import './insurance_bot.css';

const InsuranceBot = () => {
  const [messages, setMessages] = useState([{ text: "Welcome! üá∫üá∏ How can I assist you with insurance or healthcare plans today?", sender: "bot" }]);
  const [input, setInput] = useState('');
  const [memory, setMemory] = useState([]);
  const [listening, setListening] = useState(false);
  const recognitionRef = useRef(null);

  useEffect(() => {
    if ('webkitSpeechRecognition' in window) {
      const recognition = new window.webkitSpeechRecognition();
      recognition.continuous = false;
      recognition.interimResults = false;
      recognition.lang = 'en-US';

      recognition.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        handleSend(transcript);
      };

      recognition.onend = () => {
        setListening(false);
      };

      recognitionRef.current = recognition;
    }
  }, []);

  const handleMicClick = () => {
    if (listening) {
      recognitionRef.current.stop();
      setListening(false);
    } else {
      recognitionRef.current.start();
      setListening(true);
    }
  };

  const handleSend = async (customInput) => {
    const userInput = customInput || input;
    if (!userInput.trim()) return;

    const newMessage = { text: userInput, sender: 'user' };
    setMessages((prev) => [...prev, newMessage]);
    setMemory((prev) => [...prev, userInput]);
    setInput('');

    try {
      const response = await fetch('http://127.0.0.1:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: userInput }),
      });

      const data = await response.json();
      const botResponse = data.response || "Sorry, I couldn't process your request.";

      setMessages((prev) => [...prev, { text: botResponse, sender: 'bot' }]);

      if (listening) {
        const utterance = new SpeechSynthesisUtterance(botResponse);
        window.speechSynthesis.speak(utterance);
      }

    } catch (error) {
      setMessages((prev) => [...prev, { text: "‚ö†Ô∏è Sorry, something went wrong.", sender: 'bot' }]);
    }
  };

  return (
    <div className="full-container">
      <div className="memory-sidebar">
        <h2>üß† Memory</h2>
        {memory.map((item, idx) => (
          <div key={idx} className="memory-item">{item.slice(0, 20)}...</div>
        ))}
      </div>

      <div className="chat-container">
        <div className="chat-header">ü§ñ Health Insurance Assistant</div>

        <div className="chat-messages">
          {messages.map((msg, idx) => (
            <div key={idx} className={`message ${msg.sender}`}>
              <span dangerouslySetInnerHTML={{ __html: msg.text }} />
            </div>
          ))}
        </div>

        <div className="chat-input">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your message..."
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
          />
          <button className="send-button" onClick={() => handleSend()}>Send</button>
          <button
            className={`mic-button ${listening ? 'listening' : ''}`}
            onClick={handleMicClick}
          >
            üé§
          </button>
        </div>
      </div>
    </div>
  );
};

export default InsuranceBot;
