import React, { useState } from 'react';
import axios from 'axios';

const InsuranceChatbot = () => {
  const [userInput, setUserInput] = useState('');
  const [messages, setMessages] = useState([
    { sender: 'bot', text: 'üëã Hi there! How can I assist you today with insurance or healthcare plans?' }
  ]);

  const handleSend = async () => {
    if (!userInput.trim()) return;

    const newMessages = [...messages, { sender: 'user', text: userInput }];
    setMessages(newMessages);

    try {
      const response = await axios.post('http://127.0.0.1:8000/chat', {
        message: userInput
      });
      const botReply = response.data.response;

      setMessages(prevMessages => [
        ...prevMessages,
        { sender: 'bot', text: botReply }
      ]);
    } catch (error) {
      console.error('Error communicating with backend:', error);
      setMessages(prevMessages => [
        ...prevMessages,
        { sender: 'bot', text: '‚ö†Ô∏è Sorry, something went wrong. Please try again later.' }
      ]);
    }

    setUserInput('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>üè• Insurance Assistant Bot</h1>
      <div style={styles.chatBox}>
        {messages.map((msg, idx) => (
          <div
            key={idx}
            style={{
              ...styles.message,
              alignSelf: msg.sender === 'user' ? 'flex-end' : 'flex-start',
              backgroundColor: msg.sender === 'user' ? '#DCF8C6' : '#E6E6E6'
            }}
          >
            {msg.text}
          </div>
        ))}
      </div>
      <div style={styles.inputContainer}>
        <input
          type="text"
          placeholder="Type your message..."
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          onKeyDown={handleKeyPress}
          style={styles.input}
        />
        <button onClick={handleSend} style={styles.sendButton}>
          Send
        </button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: '100vh',
    display: 'flex',
    flexDirection: 'column',
    background: 'linear-gradient(to right, #e0eafc, #cfdef3)',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '20px'
  },
  title: {
    marginBottom: '20px',
    color: '#333',
  },
  chatBox: {
    width: '100%',
    maxWidth: '600px',
    height: '60vh',
    backgroundColor: '#fff',
    borderRadius: '10px',
    padding: '15px',
    overflowY: 'auto',
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
    marginBottom: '15px',
    boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)'
  },
  message: {
    padding: '10px 15px',
    borderRadius: '20px',
    maxWidth: '75%',
    fontSize: '16px',
    lineHeight: '1.4'
  },
  inputContainer: {
    display: 'flex',
    width: '100%',
    maxWidth: '600px',
    gap: '10px'
  },
  input: {
    flexGrow: 1,
    padding: '10px',
    borderRadius: '20px',
    border: '1px solid #ccc',
    fontSize: '16px'
  },
  sendButton: {
    padding: '10px 20px',
    borderRadius: '20px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    border: 'none',
    cursor: 'pointer',
    fontWeight: 'bold'
  }
};

export default InsuranceChatbot;
