import React, { useState, useEffect, useRef } from "react";
import "../styles/insurance_bot.css";
import { FaMicrophone, FaMicrophoneSlash } from "react-icons/fa";

const InsuranceBot = () => {
  const [messages, setMessages] = useState([{ from: "bot", text: "\ud83c\udf0a Welcome! How can I assist you with insurance or healthcare plans today?" }]);
  const [input, setInput] = useState("");
  const [isListening, setIsListening] = useState(false);
  const recognitionRef = useRef(null);

  useEffect(() => {
    if ("webkitSpeechRecognition" in window) {
      recognitionRef.current = new window.webkitSpeechRecognition();
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = false;
      recognitionRef.current.lang = "en-US";

      recognitionRef.current.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setInput(transcript);
      };

      recognitionRef.current.onerror = (event) => {
        console.error("Speech recognition error", event.error);
        setIsListening(false);
      };
    }
  }, []);

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { from: "user", text: input }];
    setMessages(newMessages);

    try {
      const response = await fetch("http://localhost:8000/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: input }),
      });
      const data = await response.json();

      setMessages((prev) => [...prev, { from: "bot", text: data.response }]);
    } catch (error) {
      setMessages((prev) => [...prev, { from: "bot", text: "⚠️ Error. Please try again later." }]);
    }

    setInput("");
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      handleSend();
    }
  };

  const toggleMic = () => {
    if (isListening) {
      recognitionRef.current.stop();
      setIsListening(false);
    } else {
      recognitionRef.current.start();
      setIsListening(true);
    }
  };

  return (
    <div className="full-container">
      <div className="memory-sidebar">
        <h2>🧠 Memory</h2>
        {messages.filter(msg => msg.from === "user").map((msg, index) => (
          <div key={index} className="memory-item">
            {msg.text.length > 20 ? msg.text.slice(0, 20) + "..." : msg.text}
          </div>
        ))}
      </div>
      <div className="chat-container">
        <div className="chat-header">🤖 Health Insurance Assistant</div>
        <div className="chat-messages">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`message ${msg.from}`}
              dangerouslySetInnerHTML={{ __html: msg.text.replace(/\n/g, "<br>") }}
            />
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Type your message..."
          />
          <button className="send-button" onClick={handleSend}>Send</button>
          <button
            className={`mic-button ${isListening ? "listening" : ""}`}
            onClick={toggleMic}
          >
            {isListening ? <FaMicrophoneSlash /> : <FaMicrophone />}
          </button>
        </div>
      </div>
    </div>
  );
};

export default InsuranceBot;
