import React, { useState, useEffect, useRef } from 'react';

const InsuranceBot = () => {
  const [messages, setMessages] = useState([
    { sender: 'bot', text: 'üëã Hi there! How can I assist you today with insurance or healthcare plans?' }
  ]);
  const [input, setInput] = useState('');
  const chatEndRef = useRef(null);

  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async () => {
    if (input.trim() === '') return;
    setMessages(prev => [...prev, { sender: 'user', text: input }]);

    try {
      const res = await fetch('http://127.0.0.1:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input })
      });

      const data = await res.json();

      const botReply = typeof data.response === 'string'
        ? data.response
        : JSON.stringify(data.response); // Safely handle if backend sends an object

      setMessages(prev => [...prev, { sender: 'bot', text: botReply }]);
      setInput('');
    } catch (error) {
      console.error(error);
      setMessages(prev => [...prev, { sender: 'bot', text: "‚ö†Ô∏è Sorry, something went wrong." }]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  return (
    <div style={{ backgroundColor: '#dbeafe', minHeight: '100vh', padding: '2rem' }}>
      <h1 style={{ textAlign: 'center', marginBottom: '1rem' }}>üè• Insurance Assistant Bot</h1>

      <div style={{
        maxWidth: '700px',
        margin: '0 auto',
        backgroundColor: 'white',
        borderRadius: '15px',
        padding: '1rem',
        minHeight: '500px',
        display: 'flex',
        flexDirection: 'column',
        gap: '0.8rem',
        overflowY: 'auto'
      }}>
        {messages.map((msg, idx) => (
          <div
            key={idx}
            style={{
              alignSelf: msg.sender === 'user' ? 'flex-end' : 'flex-start',
              backgroundColor: msg.sender === 'user' ? '#d1fae5' : '#e5e7eb',
              padding: '10px 15px',
              borderRadius: '20px',
              maxWidth: '80%',
              whiteSpace: 'pre-line'
            }}
          >
            {msg.text}
          </div>
        ))}
        <div ref={chatEndRef} />
      </div>

      <div style={{
        maxWidth: '700px',
        margin: '2rem auto 0',
        display: 'flex',
        alignItems: 'center',
        gap: '0.5rem'
      }}>
        <input
          type="text"
          placeholder="Type your message..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={handleKeyPress}
          style={{
            flex: 1,
            padding: '0.8rem',
            borderRadius: '25px',
            border: '1px solid #ccc',
            outline: 'none'
          }}
        />
        <button
          onClick={sendMessage}
          style={{
            padding: '0.8rem 1.5rem',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '25px',
            cursor: 'pointer',
            fontWeight: 'bold'
          }}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default InsuranceBot;
