import React, { useState, useEffect, useRef } from 'react';
import './insurance_bot.css';

const InsuranceBot = () => {
  const [messages, setMessages] = useState([
    { text: "ðŸ‘‹ Welcome! How can I assist you with insurance or healthcare plans today?", sender: "bot" }
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const [isListening, setIsListening] = useState(false);
  const recognitionRef = useRef(null);
  const lastInteractionWasVoice = useRef(false);

  useEffect(() => {
    if ('webkitSpeechRecognition' in window) {
      const recognition = new window.webkitSpeechRecognition();
      recognition.lang = 'en-US';
      recognition.continuous = false;
      recognition.interimResults = false;

      recognition.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setInputMessage(transcript);
        handleSend(transcript, true);  // true means it came from voice
      };

      recognition.onend = () => {
        setIsListening(false);
      };

      recognitionRef.current = recognition;
    }
  }, []);

  const handleMicClick = () => {
    if (isListening) {
      recognitionRef.current.stop();
      setIsListening(false);
    } else {
      recognitionRef.current.start();
      setIsListening(true);
    }
  };

  const handleSend = async (optionalMessage = null, fromVoice = false) => {
    const messageToSend = optionalMessage || inputMessage.trim();
    if (!messageToSend) return;

    const userMessage = { text: messageToSend, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    lastInteractionWasVoice.current = fromVoice; // Track voice or text input

    try {
      const response = await fetch('http://127.0.0.1:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: messageToSend })
      });
      const data = await response.json();
      const botMessage = { text: data.response, sender: 'bot' };
      setMessages(prev => [...prev, botMessage]);

      if (fromVoice) {
        speakText(data.response); // Only if mic was used
      }
    } catch (error) {
      const errorMessage = { text: "âš ï¸ Error! Please try again later.", sender: "bot" };
      setMessages(prev => [...prev, errorMessage]);
    }
  };

  const speakText = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = 'en-US';
    window.speechSynthesis.speak(utterance);
  };

  return (
    <div className="chat-layout">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>ðŸ§  Memory</h2>
        <ul>
          {messages
            .filter(msg => msg.sender === 'user')
            .map((msg, idx) => (
              <li key={idx}>{msg.text.slice(0, 20)}...</li>
            ))}
        </ul>
      </div>

      {/* Chat Area */}
      <div className="chat-container">
        <div className="chat-title">
          <span role="img" aria-label="bot">ðŸ¤–</span> Health Insurance Assistant
        </div>
        <div className="chat-box">
          {messages.map((msg, idx) => (
            <div key={idx} className={`chat-message ${msg.sender}`}>
              {msg.text}
            </div>
          ))}
        </div>
        <div className="input-section">
          <input
            type="text"
            className="chat-input"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Type your message..."
            onKeyDown={(e) => e.key === 'Enter' && handleSend()}
          />
          <button className="send-button" onClick={() => handleSend()}>Send</button>
          <button
            className={`mic-button ${isListening ? 'listening' : ''}`}
            onClick={handleMicClick}
          >
            ðŸŽ¤
          </button>
        </div>
      </div>
    </div>
  );
};

export default InsuranceBot;
