import React, { useState, useEffect, useRef } from "react";
import "./insurance_bot.css";

const InsuranceBot = () => {
  const [messages, setMessages] = useState([
    { text: "ðŸ‘‹ Welcome! How can I assist you with health insurance or healthcare plans in the USA today?", sender: "bot" }
  ]);
  const [inputMessage, setInputMessage] = useState("");
  const [listening, setListening] = useState(false);
  const recognitionRef = useRef(null);

  useEffect(() => {
    if ("webkitSpeechRecognition" in window) {
      const recognition = new window.webkitSpeechRecognition();
      recognition.continuous = false;
      recognition.lang = "en-US";
      recognition.interimResults = false;
      recognition.maxAlternatives = 1;

      recognition.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        handleUserMessage(transcript, true);
      };

      recognition.onerror = (event) => {
        console.error("Speech Recognition Error", event.error);
        setListening(false);
      };

      recognition.onend = () => {
        setListening(false);
      };

      recognitionRef.current = recognition;
    }
  }, []);

  const handleUserMessage = async (message, isVoice = false) => {
    if (!message.trim()) return;

    setMessages((prev) => [...prev, { text: message, sender: "user" }]);
    setInputMessage("");

    try {
      const response = await fetch("http://127.0.0.1:8000/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message })
      });

      const data = await response.json();
      const botResponse = data.response;

      setMessages((prev) => [...prev, { text: botResponse, sender: "bot" }]);

      if (isVoice && window.speechSynthesis) {
        const utterance = new SpeechSynthesisUtterance(botResponse);
        window.speechSynthesis.speak(utterance);
      }

    } catch (error) {
      console.error("Error fetching response:", error);
      setMessages((prev) => [...prev, { text: "âš ï¸ Sorry, something went wrong.", sender: "bot" }]);
    }
  };

  const handleSendClick = () => {
    if (inputMessage.trim()) {
      handleUserMessage(inputMessage);
    }
  };

  const handleMicClick = () => {
    if (!recognitionRef.current) return;
    if (listening) {
      recognitionRef.current.stop();
      setListening(false);
    } else {
      recognitionRef.current.start();
      setListening(true);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      handleSendClick();
    }
  };

  return (
    <div className="full-container">
      <div className="chat-container">
        <div className="chat-header">
          ðŸ¤– Health Insurance Assistant
        </div>

        <div className="chat-messages">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`message ${msg.sender}`}
              dangerouslySetInnerHTML={{ __html: msg.text.replace(/\n/g, "<br/>") }}
            />
          ))}
        </div>

        <div className="chat-input">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Type your message..."
          />
          <button className="send-button" onClick={handleSendClick}>
            Send
          </button>
          <button
            className={`mic-button ${listening ? "listening" : ""}`}
            onClick={handleMicClick}
          >
            ðŸŽ¤
          </button>
        </div>
      </div>
    </div>
  );
};

export default InsuranceBot;
