import React, { useState } from 'react';

const InsuranceBot = () => {
  const [messages, setMessages] = useState([
    "👋 Hi there! How can I assist you today with insurance or healthcare plans?"
  ]);
  const [input, setInput] = useState('');

  const sendMessage = async () => {
    if (input.trim() === '') return;
    setMessages(prev => [...prev, input]);

    try {
      const res = await fetch('http://127.0.0.1:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input })
      });

      const data = await res.json();
      setMessages(prev => [...prev, data.response]); // ✅ Only use "data.response" here
      setInput('');
    } catch (error) {
      console.error(error);
      setMessages(prev => [...prev, "⚠️ Sorry, something went wrong. Please try again later."]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  return (
    <div style={{ backgroundColor: '#dbeafe', minHeight: '100vh', padding: '2rem' }}>
      <h1 style={{ textAlign: 'center', marginBottom: '1rem' }}>
        📅 Insurance Assistant Bot
      </h1>

      <div style={{
        maxWidth: '700px',
        margin: '0 auto',
        backgroundColor: 'white',
        borderRadius: '15px',
        padding: '1rem',
        minHeight: '500px',
        display: 'flex',
        flexDirection: 'column',
        gap: '0.8rem'
      }}>
        {messages.map((msg, idx) => (
          <div
            key={idx}
            style={{
              alignSelf: idx % 2 === 0 ? 'flex-start' : 'flex-end',
              backgroundColor: idx % 2 === 0 ? '#e5e7eb' : '#d1fae5',
              padding: '0.7rem 1rem',
              borderRadius: '20px',
              maxWidth: '80%',
              whiteSpace: 'pre-line'
            }}
          >
            {msg}
          </div>
        ))}
      </div>

      <div style={{
        maxWidth: '700px',
        margin: '2rem auto 0',
        display: 'flex',
        alignItems: 'center',
        gap: '0.5rem'
      }}>
        <input
          type="text"
          placeholder="Type your message..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={handleKeyPress}
          style={{
            flex: 1,
            padding: '0.8rem',
            borderRadius: '25px',
            border: '1px solid #ccc',
            outline: 'none'
          }}
        />
        <button
          onClick={sendMessage}
          style={{
            padding: '0.8rem 1.5rem',
            backgroundColor: '#22c55e',
            color: 'white',
            border: 'none',
            borderRadius: '25px',
            cursor: 'pointer',
            fontWeight: 'bold'
          }}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default InsuranceBot;
